import React, { useContext, useEffect } from "react";
import { View, FlatList, Text } from "react-native";
import ConcellContext from "../../context/Context";
import { Style } from "./SharedStyle";
import Room from "./Room";

export default function HomeScreen({ navigation }) {
  const { GetRooms, isRoom, isState } = useContext(ConcellContext);

  useEffect(() => {
    GetRooms();
  }, []);

  const custom_list_header = () => {
    return (
      <View style={Style.custom_list_header}>
        <Text style={Style.custom_list_header_title}>Rooms</Text>
      </View>
    );
  };

  const custom_list_footer = () => {
    return <View style={Style.custom_room_list_footer} />;
  };

  return (
    <View style={Style.home_container}>
      <FlatList
        onRefresh={() => GetRooms()}
        refreshing={isState.loading}
        showsVerticalScrollIndicator={false}
        data={isRoom}
        renderItem={({ item }) => <Room item={item} navigation={navigation} />}
        keyExtractor={(item) => item.id}
        ListHeaderComponent={custom_list_header}
        ListFooterComponent={custom_list_footer}
      />
    </View>
  );
}
