import { getFocusedRouteNameFromRoute } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { useLayoutEffect } from "react";
import MembersScreen from "../shared/Member";
import HomeScreen from "../shared/Home";
import RoomDetailsScreen from "../shared/RoomDetails";
import useCustomHeader from "../shared/useCustomHeader";

const HomeScreenStack = createNativeStackNavigator();

export default function HomeStack({ navigation, route }) {
  const noTabs = ["Members", "Room_Details"];
  useLayoutEffect(() => {
    if (noTabs.includes(getFocusedRouteNameFromRoute(route))) {
      navigation.setOptions({
        tabBarStyle: { display: "none" },
      });
    } else {
      navigation.setOptions({ tabBarStyle: { position: "absolute" } });
    }
  }, [navigation, route]);

  return (
    <HomeScreenStack.Navigator
      initialRouteName="Room_Home"
      screenOptions={{
        animation: "slide_from_right",
        animationDuration: 500,
        statusBarColor: "white",
      }}
    >
      <HomeScreenStack.Screen
        name="Room_Home"
        component={HomeScreen}
        options={{
          headerTitleAlign: "left",
          headerTitle: "Concell",
          headerTitleStyle: {
            fontWeight: "700",
            fontSize: 22,
          },
          headerRight: useCustomHeader,
        }}
      />
      <HomeScreenStack.Screen
        name="Room_Details"
        component={RoomDetailsScreen}
        options={{
          headerTitle: "Details",
          headerTitleAlign: "center",
          headerShadowVisible: false,
          headerTitleStyle: {
            fontSize: 18,
          },
        }}
      />
      <HomeScreenStack.Screen
        name="Members"
        component={MembersScreen}
        options={{
          presentation: "containedTransparentModal",
        }}
      />
    </HomeScreenStack.Navigator>
  );
}
