import { View, Text, Image, TouchableOpacity } from "react-native";
import { Style } from "./SharedStyle";
import room_image from "../assets/room_image.jpg";

export default function Room({ item, navigation }) {
  return (
    <TouchableOpacity
      style={Style.room_wrapper}
      onPress={() =>
        navigation.push("Room_Details", {
          item: item,
        })
      }
    >
      <View style={Style.room_icon}>
        <View style={Style.room_icon_container}>
          {item.name ? (
            <Image style={Style.room_background_image} source={room_image} />
          ) : (
            <Text style={Style.room_icon_title}>IMS</Text>
          )}
        </View>
      </View>
      <View style={Style.room_wrapper_container}>
        <View style={Style.room_title_wrapper}>
          <Text style={Style.room_title} numberOfLines={1}>
            {item.name}
          </Text>
        </View>
        <View style={Style.room_details}>
          <Text style={Style.room_details_description} numberOfLines={2}>
            {item.body}
          </Text>
          <View style={Style.room_user_members}>
            <View style={Style.room_users_image}>
              <Text style={Style.room_user_title}>
                {item.name.substring(0, 2).toUpperCase()}
              </Text>
              {/* <Image style={Style.room_user_image} source={room_image} /> */}
            </View>
            <View style={Style.room_users_image}>
              <Text style={Style.room_user_title}>
                {item.name.substring(0, 2).toUpperCase()}
              </Text>
              {/* <Image style={Style.room_user_image} source={room_image} /> */}
            </View>
            <View style={Style.room_users_image}>
              <Text style={Style.room_user_title}>
                {item.name.substring(0, 2).toUpperCase()}
              </Text>
              {/* <Image style={Style.room_user_image} source={room_image} /> */}
            </View>
            <View style={Style.room_users_image}>
              <Text style={Style.room_user_title}>
                {item.name.substring(0, 2).toUpperCase()}
              </Text>
              {/* <Image style={Style.room_user_image} source={room_image} /> */}
            </View>
            <View style={Style.room_users_image}>
              <Text style={Style.room_user_title}>
                {item.name.substring(0, 2).toUpperCase()}
              </Text>
              {/* <Image style={Style.room_user_image} source={room_image} /> */}
            </View>
            <View style={Style.rooms_user_count}>
              <Text style={Style.rooms_user_count_title}>26+ members</Text>
            </View>
          </View>
        </View>
      </View>
    </TouchableOpacity>
  );
}
