import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import SettingsScreen from "../shared/Settings";
import NotificationScreen from "../shared/Notification";
import Ionicons from "react-native-vector-icons/Ionicons";
import ScheduleStacksScreen from "../Schedule";
import HomeStack from "../Home";

export default function StudentScreen() {
  const Tab = createBottomTabNavigator();

  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          if (route.name === "Home") {
            iconName = focused ? "ios-home" : "ios-home-outline";
          } else if (route.name === "Notification") {
            iconName = focused
              ? "ios-notifications-circle"
              : "ios-notifications-circle-outline";
          } else if (route.name === "Settings") {
            iconName = focused ? "ios-settings-sharp" : "ios-settings-outline";
          } else if (route.name === "Schedule") {
            iconName = focused ? "ios-list-sharp" : "ios-list-outline";
          }

          return <Ionicons name={iconName} size={size} color={color} />;
        },
        tabBarActiveTintColor: "#379237",
        tabBarInactiveTintColor: "gray",
        headerStyle: {
          borderBottomWidth: 1,
          borderBottomColor: "#E9E9E9",
        },
      })}
    >
      <Tab.Screen
        name="Home"
        component={HomeStack}
        options={{
          headerShown: false,
        }}
      />
      <Tab.Screen
        name="Schedule"
        options={{
          headerShown: false,
        }}
        component={ScheduleStacksScreen}
      />
      <Tab.Screen name="Notification" component={NotificationScreen} />
      <Tab.Screen name="Settings" component={SettingsScreen} />
    </Tab.Navigator>
  );
}
