import { createContext, useReducer, useMemo, useState } from "react";
import { Reducer, InitialState } from "./Reducer";
import { ACTION_TYPES } from "./ActionType";

const ConcellContext = createContext();

export function ConcellProvider({ children }) {
  const [isState, setDispatch] = useReducer(Reducer, InitialState);
  const [isRoom, setRoom] = useState([]);

  const ActionType = useMemo(
    () => ({
      FETCH_LOGIN: async () => {
        setDispatch({ type: ACTION_TYPES.FETCH_LOGIN });
      },
      FETCH_LOGOUT: async () => {
        setDispatch({ type: ACTION_TYPES.FETCH_LOGOUT });
      },
      FETCH_START: async () => {
        setDispatch({ type: ACTION_TYPES.FETCH_START });
      },
      FETCH_STOP: async () => {
        setDispatch({ type: ACTION_TYPES.FETCH_STOP });
      },
    }),
    []
  );

  const GetRooms = async () => {
    ActionType.FETCH_START();
    await fetch("https://jsonplaceholder.typicode.com/comments?_limit=10", {
      method: "GET",
      redirect: "follow",
    })
      .then((response) => response.json())
      .then((result) => {
        setRoom(result);
        ActionType.FETCH_STOP();
      })
      .catch((error) => console.log("error", error));
  };

  return (
    <ConcellContext.Provider
      value={{
        isState,
        ActionType,
        GetRooms,
        isRoom,
      }}
    >
      {children}
    </ConcellContext.Provider>
  );
}

export default ConcellContext;
