import { ACTION_TYPES } from "./ActionType";

export const InitialState = {
  loading: false,
  status: false,
  student: false,
  admin: false,
  faculty: false,
  token: "",
};

export const Reducer = (prevState, action) => {
  switch (action.type) {
    case ACTION_TYPES.FETCH_LOGIN:
      return {
        ...prevState,
        status: true,
      };
    case ACTION_TYPES.FETCH_LOGOUT:
      return {
        ...prevState,
        status: false,
      };
    case ACTION_TYPES.FETCH_START:
      return {
        ...prevState,
        loading: true,
      };
    case ACTION_TYPES.FETCH_STOP:
      return {
        ...prevState,
        loading: false,
      };
    default:
      return prevState;
  }
};
