import React, { useContext, useEffect } from "react";
import { View, Text, FlatList, TouchableOpacity } from "react-native";
import ConcellContext from "../../context/Context";
import EvilIcons from "react-native-vector-icons/EvilIcons";
import { Style } from "./SharedStyle";

export default function ScheduleMainScreen({ navigation }) {
  const { GetRooms, isRoom, isState } = useContext(ConcellContext);

  useEffect(() => {
    GetRooms();
  }, []);

  const ScheduleComponent = ({ item }) => {
    const useDate = new Date();
    return (
      <TouchableOpacity style={Style.schedule_component_container}>
        <View style={Style.schedule_component_container_icon}>
          <EvilIcons name="exclamation" size={30} />
        </View>
        <View style={Style.schedule_component_container_titles}>
          <Text style={Style.schedule_component_title} numberOfLines={1}>
            {item.name}
          </Text>
          <View style={Style.schedule_time_date}>
            <Text style={Style.schedule_component_time}>
              8:00 AM - 10:00 AM
            </Text>
            <Text style={Style.schedule_component_date}>
              {useDate.toDateString()}
            </Text>
          </View>
        </View>
      </TouchableOpacity>
    );
  };

  const custom_list_header = () => {
    return (
      <View style={Style.custom_list_header}>
        <Text style={Style.custom_list_header_title}>Recent</Text>
      </View>
    );
  };

  return (
    <View style={Style.main_schedule_container}>
      <FlatList
        onRefresh={() => GetRooms()}
        refreshing={isState.loading}
        showsVerticalScrollIndicator={false}
        data={isRoom}
        renderItem={ScheduleComponent}
        keyExtractor={(item) => item.id}
        ListHeaderComponent={custom_list_header}
      />
    </View>
  );
}
