import React, { useState, useContext } from "react";
import {
  View,
  Text,
  TextInput,
  KeyboardAvoidingView,
  TouchableOpacity,
} from "react-native";
import Checkbox from "expo-checkbox";
import { Style } from "./AuthStyle";
import ConcellContext from "../../context/Context";

export default function Login({ navigation }) {
  const [isChecked, setChecked] = useState(false);
  const { ActionType } = useContext(ConcellContext);

  return (
    <KeyboardAvoidingView style={Style._login_container}>
      <View style={Style._login_wrapper}>
        <View style={Style._top_wrapper}>
          <View style={Style._title_wrapper}>
            <Text style={Style._header_title}>Log in</Text>
          </View>
          <View style={Style._input_wrapper}>
            <View style={Style._input_wrapper_container}>
              <Text style={Style._input_title}>Student No.</Text>
              <TextInput
                style={Style._input_number}
                keyboardType="numeric"
                selectionColor="#585757"
              />
            </View>
            <View style={Style._input_wrapper_container}>
              <Text style={Style._input_title}>Password</Text>
              <TextInput
                style={Style._input_password}
                selectionColor="#585757"
              />
            </View>
          </View>
        </View>
        <View style={Style._checkbox_wrapper}>
          <Checkbox
            style={Style._checkbox}
            value={isChecked}
            onValueChange={setChecked}
            color={isChecked ? "#3CA2FA" : undefined}
          />
          <Text style={Style._checkbox_title}>Remember me</Text>
        </View>
        <TouchableOpacity
          style={Style._login_btn}
          onPress={() => ActionType.FETCH_LOGIN()}
        >
          <Text style={Style._login_title}>Log in</Text>
        </TouchableOpacity>
        <TouchableOpacity style={Style._forgot}>
          <Text style={Style._forgot_title}>Forgot Password?</Text>
        </TouchableOpacity>
        <View style={Style._line} />
        <View style={Style._signup_wrapper}>
          <Text style={Style._sign_account}>Don't have an account?</Text>
          <TouchableOpacity onPress={() => navigation.push("Signup")}>
            <Text style={Style.sign_title_link}>Sign up</Text>
          </TouchableOpacity>
        </View>
      </View>
    </KeyboardAvoidingView>
  );
}
